import{collection,getDocs}from"https://www.gstatic.com/firebasejs/10.12.0/firebase-firestore.js";import{db}from"../firebase-config.js";async function loadFooterData(){try{(await getDocs(collection(db,"footer"))).forEach(e=>{const t=e.data();document.getElementById("facebook-link").href=t.facebook,document.getElementById("instagram-link").href=t.instagram,document.getElementById("email-link").textContent=t.email,document.getElementById("email-link").href=`mailto:${t.email}`,document.getElementById("telephone").textContent=t.telephone})}catch(e){console.error("Error memuat data footer:",e)}}window.onload=function(){loadFooterData()};