import{collection,getDocs,query,where}from"https://www.gstatic.com/firebasejs/10.12.0/firebase-firestore.js";import{db}from"../firebase-config.js";const urlParams=new URLSearchParams(window.location.search),slug=urlParams.get("slug");async function loadNews(){if(slug)try{const e=query(collection(db,"berita"),where("slug","==",slug)),t=await getDocs(e);if(t.empty)document.body.innerHTML="<h1>Berita tidak ditemukan!</h1>";else{const e=t.docs[0].data();document.getElementById("title").innerText=e.title,document.getElementById("content").innerHTML=e.content}}catch(e){console.error("Gagal memuat berita:",e)}else document.body.innerHTML="<h1>Slug tidak ditemukan di URL!</h1>"}document.addEventListener("DOMContentLoaded",loadNews);