function levenshteinDistance(e,t){const n=[];for(let e=0;e<=t.length;e++)n[e]=[e];for(let t=0;t<=e.length;t++)n[0][t]=t;for(let l=1;l<=t.length;l++)for(let s=1;s<=e.length;s++)t.charAt(l-1)===e.charAt(s-1)?n[l][s]=n[l-1][s-1]:n[l][s]=Math.min(n[l-1][s-1]+1,n[l][s-1]+1,n[l-1][s]+1);return n[t.length][e.length]}function search(){const e=document.getElementById("searchInput").value.toLowerCase(),t=document.getElementById("data-table").getElementsByTagName("tr");let n=!1;const l=[];for(let s=1;s<t.length;s++){const a=t[s].getElementsByTagName("td")[2];if(a){const o=a.textContent||a.innerText,c=o.toLowerCase();if(c.indexOf(e)>-1)t[s].style.display="",n=!0;else{t[s].style.display="none";const n=levenshteinDistance(e,c);l.push({row:t[s],distance:n,name:o})}}}if(n){const e=document.getElementById("searchHint");e&&(e.style.display="none")}else{l.sort((e,t)=>e.distance-t.distance);const e=3;let t=0;const n=document.getElementById("searchHint");n&&(n.style.display="block");for(let n=0;n<l.length&&t<e;n++)l[n].row.style.display="",t++;0===t&&(alert("Data tidak ditemukan."),n&&(n.style.display="none"))}}function cancelSearch(){document.getElementById("searchInput").value="";const e=document.getElementById("data-table").getElementsByTagName("tr");for(let t=1;t<e.length;t++)e[t].style.display="";const t=document.getElementById("searchHint");t&&(t.style.display="none")}document.addEventListener("DOMContentLoaded",function(){document.getElementById("searchInput").addEventListener("input",function(){if(""===this.value){const e=document.getElementById("data-table").getElementsByTagName("tr");for(let t=1;t<e.length;t++)e[t].style.display="";const t=document.getElementById("searchHint");t&&(t.style.display="none")}})});